import React from 'react';
import './Explore.css';
import post1 from '../../images/pp1.jpg';
import { Component } from 'react';

class Explore extends Component{
    constructor(props) {
        super(props);
        this.state = {  
            arrayRecentPost:[],
            arrayPopularPost:[],
            isRecents: true,
            isPopular: false
        }
    }

    componentDidMount(){
        this.getRecentPost();
        this.getPopularPost();
    }

    // changeRecents=()=>{
    //     if(this.isRecents){
    //         this.setState({isRecents:false})
    //         this.getPopularPost();
    //     }
    //     else{
    //         this.setState({isRecents:true})
    //         this.getRecentPost();
    //     }
    // }

    handleRecents(){
        this.isRecents(true);
        this.isPopular(false);
        this.getRecentPost();
    }
    handlePopular(){
        this.isPopular(true);
        this.isRecents(false);
        this.getPopularPost();

    }

    getRecentPost=()=>{
        const thisContext = this;
        fetch("http://localhost:8080/post/recents/" + JSON.parse(localStorage.getItem("users")).uid)             //get post of following users
            .then(response => response.json())
            .then(data => {
                console.log(data);
                thisContext.setState({arrayRecentPost: data});
        });
    }

    getPopularPost=()=>{
        const thisContext = this;
        fetch("http://localhost:8080/post/popular/" + JSON.parse(localStorage.getItem("users")).uid)             //get post of following users
            .then(response => response.json())
            .then(data => {
                console.log(data);
                thisContext.setState({arrayPopularPost: data});
        });
    }

    render(){
        return (
            <div className='explore_container'>
                <div className='explore_titles'>
                    <div onClick={this.handleRecents}>Recents</div>
                    <div onClick={this.handlePopular}>Popular</div>
                </div>
                <div className='explore'>
                    {
                        this.state.arrayRecentPost.map((item, index) => {
                        return(
                            <div className='explore_posts' key={index}>
                                <img src={item.path} style={{width: "100%"}}></img>
                            </div>
                        )
                        })
                    } 
                    {
                        this.state.arrayPopularPost.map((item, index) => {
                            return(
                                <div className='explore_posts' key={index}>
                                    <img src={item.path} style={{width: "100%"}}></img>
                                </div>
                            )
                        })
                    }
                </div>
            </div>
          )
    }
}


export default Explore
