import React, { useState } from 'react';
import MainPage from '../MainPage/MainPage';
import { Grid } from '@mui/material';
import './Home.css';
import { useNavigate } from 'react-router-dom';
import StatusBar from '../StatusBar/StatusBar';

function Home() {
    const navigate = useNavigate();
    const [showHomeContent, setShowHomeContent] = useState(true);

    const handleHomeButton=()=>{
        showHomeContent(true);
    }

  return (
    <div className='home_container'>
        <Grid container>
            <Grid item xs={1.5}>
                <div className='home_sidebar'>
                    <img></img>
                    <button className='sidebar_btn' onClick={handleHomeButton}>Home</button>
                    <button className='sidebar_btn'>Search</button>
                    <button className='sidebar_btn'>Explore</button>
                </div>
            </Grid>
            <Grid item xs={10}>
                <div id='home_content'>
                    {showHomeContent && 
                        <div>
                            <StatusBar/>
                            <MainPage/>
                        </div>
                    }
                </div>
            </Grid>
        </Grid>
    </div>

  )
}

export default Home
