import React, { useEffect, useState } from 'react';
import MainPage from '../MainPage/MainPage';
import { Grid } from '@mui/material';
import './Home.css';
import { useNavigate } from 'react-router-dom';
import StatusBar from '../StatusBar/StatusBar';
import Profile from '../Profile/Profile';

function Home() {
    const navigate = useNavigate();
    const [showHomeContent, setShowHomeContent] = useState(false);
    const [showSearchContent, setShowSearchContent] = useState(false);
    const [showProfileContent, setShowProfileContent] = useState(false);
    const [followersList, setFollowersList] = useState([]);

    useEffect(() =>{
        if(showProfileContent){
            fetch("http://localhost:8080/profile/"+ JSON.parse(localStorage.getItem("users")).uid)
                .then(response => response.json())
                .then(data => {
                    setFollowersList(data);
                    console.log(data);
            });
        } 
    },[showProfileContent]);  

    const handleHomeButton=()=>{
        setShowHomeContent(true);
        // console.log("hii")
    }
    const handleSearchButton=()=>{
        setShowSearchContent(true);
    }
    const handleProfileSearch=()=>{
        setShowProfileContent(true);
        setShowHomeContent(false);
    }

  return (
    <div className='home_container'>
        <Grid container>
            <Grid item xs={3}>
                <div className='home_sidebar'>
                    <button className='sidebar_btn' onClick={handleHomeButton}>Home</button>
                    <button className='sidebar_btn' onClick={handleSearchButton}>Search</button>
                    <button className='sidebar_btn'>Explore</button>
                    <button className='sidebar_btn' onClick={handleProfileSearch}>Profile</button>
                </div>
            </Grid>
            <Grid item xs={9}>
                <div id='home_content'>
                    {showHomeContent && 
                        <div>
                            <StatusBar/>
                            <MainPage/>
                        </div>
                    }
                    {showProfileContent &&
                        <div>
                            <Profile followersList={followersList}/>
                        </div>
                    }
                </div>
            </Grid>
        </Grid>
    </div>

  )
}

export default Home
